import React, { useState } from 'react';
import { CoralogixRum } from '@coralogix/browser';

const ErrorPage = () => {
  const [errorCount, setErrorCount] = useState(0);
  const [errorMessage, setErrorMessage] = useState('');

  const generateError = () => {
    setErrorCount(errorCount + 1);
    try {
      switch (errorCount % 4) {
        case 0:
          // TypeError: undefined is not a function
          (() => null)();
          break;
        case 1:
          // ReferenceError: x is not defined
          console.log(x);
          break;
        case 2:
          // SyntaxError: Unexpected token
          eval('foo bar');
          break;
        case 3:
          // Custom Error
          throw new Error('This is a custom error');
        default:
          break;
      }
    } catch (error) {
      // Set the error message to state to display it on the screen
      setErrorMessage(`Generated error: ${error.message}`);

      // Log the error to Coralogix
      CoralogixRum.error(`Generated error: ${error.message}`, { error });
    }
  };

  return (
    <div style={{ backgroundColor: 'blue', height: '100vh', color: 'white', padding: '20px' }}>
      <h1>Error Generation Page</h1>
      <p>Click the button below to generate an error.</p>
      <button onClick={generateError} style={{ padding: '10px 20px', fontSize: '16px' }}>
        Generate Error
      </button>
      {errorMessage && (
        <div style={{ marginTop: '20px', color: 'red' }}>
          <strong>Error:</strong> {errorMessage}
        </div>
      )}
    </div>
  );
};

export default ErrorPage;
