import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const buttonStyle = {
    padding: '15px 30px',
    fontSize: '18px',
    backgroundColor: '#2196f3',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
    marginTop: '20px',
  };

  const buttonHoverStyle = {
    backgroundColor: '#1976d2',
  };

  return (
    <div style={{ backgroundColor: '#1a237e', height: '100vh', color: 'white', padding: '20px', textAlign: 'center' }}>
      <h1 style={{ fontSize: '36px', marginBottom: '10px' }}>Welcome to the Home Page</h1>
      <p style={{ fontSize: '18px', marginBottom: '20px' }}>This is the starting point of our application.</p>
      <Link to="/about">
        <button
          style={buttonStyle}
          onMouseOver={(e) => e.currentTarget.style.backgroundColor = buttonHoverStyle.backgroundColor}
          onMouseOut={(e) => e.currentTarget.style.backgroundColor = buttonStyle.backgroundColor}
        >
          Learn More About Us
        </button>
      </Link>
      <br />
      <Link to="/error-page">
        <button
          style={buttonStyle}
          onMouseOver={(e) => e.currentTarget.style.backgroundColor = buttonHoverStyle.backgroundColor}
          onMouseOut={(e) => e.currentTarget.style.backgroundColor = buttonStyle.backgroundColor}
        >
          Go to Error Page
        </button>
      </Link>
    </div>
  );
};

export default Home;
